#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\GH_Repos\myblog\node_modules\.pnpm\registry.npmmirror.com+hexo@7.0.0\node_modules\hexo\bin\node_modules;D:\GH_Repos\myblog\node_modules\.pnpm\registry.npmmirror.com+hexo@7.0.0\node_modules\hexo\node_modules;D:\GH_Repos\myblog\node_modules\.pnpm\registry.npmmirror.com+hexo@7.0.0\node_modules;D:\GH_Repos\myblog\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/GH_Repos/myblog/node_modules/.pnpm/registry.npmmirror.com+hexo@7.0.0/node_modules/hexo/bin/node_modules:/mnt/d/GH_Repos/myblog/node_modules/.pnpm/registry.npmmirror.com+hexo@7.0.0/node_modules/hexo/node_modules:/mnt/d/GH_Repos/myblog/node_modules/.pnpm/registry.npmmirror.com+hexo@7.0.0/node_modules:/mnt/d/GH_Repos/myblog/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../hexo/bin/hexo" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../hexo/bin/hexo" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../hexo/bin/hexo" $args
  } else {
    & "node$exe"  "$basedir/../hexo/bin/hexo" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
